import React, { useEffect } from 'react'
import '../styles/ImgRent.css'

const BtnSlider = ({ direction, moveSlide, onlyOne }) => {
    useEffect(() => {
        if (!onlyOne) {
            return (
                <button
                    className={
                        direction === 'next'
                            ? 'btn-slide next'
                            : 'btn-slide prev'
                    }
                    onClick={moveSlide}
                >
                    <i
                        className={
                            direction === 'next'
                                ? 'fa-solid fa-angle-right'
                                : 'fa-solid fa-angle-left'
                        }
                    ></i>
                </button>
            )
        } else {
            return
        }
    }, [])
}

export default BtnSlider
