import React from 'react'

const BtnSlider = ({ direction, moveSlide }) => {
    return (
        <button
            className={
                direction === 'next' ? 'btn-slide next' : 'btn-slide prev'
            }
            onClick={moveSlide}
        >
            <i
                className={
                    direction === 'next'
                        ? 'fa-regular fa-angle-right'
                        : 'fa-regular fa-angle-left'
                }
            ></i>
        </button>
    )
}

export default BtnSlider
