import React, { useState } from 'react'
import { useLocation } from 'react-router-dom'
import '../styles/ImgRent.css'

function ImgRent({ imgRent }) {
    const [urlId, setUrlId] = useState(useLocation().pathname.replace('/', ''))
    const [data, setData] = useState(
        require('../data/logements.json').filter(function (rent) {
            return rent.id === urlId
        })[0]
    )
    return (
        <div className="container-slider">
            {data.pictures.map((pic) => {
                return (
                    <div key={pic}>
                        <img
                            className="img-logement"
                            src={pic}
                            alt="Location"
                        />
                    </div>
                )
            })}
        </div>
    )
}

export default ImgRent
