import React, { useState } from 'react'
import '../styles/CharteItem.css'

function CharteItem({ title }) {
    const [open, setOpen] = useState(false)
    return (
        <div className="charte__card">
            <div className="charte__card__header">
                <h2>{title}</h2>
                <i
                    className="fa-solid fa-chevron-down"
                    onClick={() => {
                        setOpen(!open)
                    }}
                ></i>
            </div>
            <div
                className={`charte__card__body ${open ? 'active' : 'inactive'}`}
            >
                <p>
                    La bienveillance fait partie des valeurs fondatrices de
                    Kasa. Tout comportement discriminatoire ou de perturbation
                    du voisinage entra√Ænera une exclusion de notre plateforme.
                </p>
            </div>
        </div>
    )
}

export default CharteItem
